mv score_file_for_all_pdbs.sc score_file_for_all_pdbs.sc.OLD ; for dir_path in $(find run*/ -type d -name "X*") ; do for  pdb_path in $(find $dir_path -maxdepth 1 -type f -name "start_*pdb" ) ; do pdb_name=$(basename "$pdb_path" .pdb) ; scores=$(grep $pdb_name ${dir_path}/score.sc | tail -n 1) ; score_names=$(grep score ${dir_path}/score.sc) ; echo "$scores $pdb_path" >> score_file_for_all_pdbs.sc ; done ; done ; echo "$score_names pdb_path" > tmp.file ; cat score_file_for_all_pdbs.sc >> tmp.file ; mv tmp.file score_file_for_all_pdbs.sc
